e) === false)\n									{\n										$.get(''https://gamification.prestashop.com/get_campaign.php'', {iso_country: country_iso_code, campaign: ''gadwords'', ps_version: _PS_VERSION_, host: totd_hostname}, function(response){\n											try {\n												var error = response.error;\n											    var code = response.code;\n											    if (!error){\n											    	$(''#totd_gadword_code'').text(code);\n											    	$(''#''+rand_section+''_googleadwords'').show();\n											    }\n											    else\n											   		$(''#0_googleadwords'').show();\n											}\n											catch(err) {\n											   $(''#0_googleadwords'').show();\n											}\n\n										}).fail(function(){\n											$(''#0_googleadwords'').show();\n										});\n									}\n									else\n										$(''#0_googleadwords'').show();\n								}\n								else if (rand_section == 1)\n									$(''#0_googleadwords'').show();\n								else\n									$(''#''+rand_section+''_googleadwords'').show();\n				});\n\n				function rand(nbr){\n				   return Math.floor(Math.random()*(nbr+1));\n				}\n			</script>\n			</div>');
