(141, 1, 1, '<p>Un magnifique coffret tire-bouchon en forme de bouteille de vin.</p>\n<div class="rte">\n<p>Deux modèle disponible : Dan FENG &amp; Vignoble décor</p>\n</div>', '<div class="rte">\n<p>Un magnifique coffret tire-bouchon en forme de bouteille de vin.</p>\n<p>Deux modèle disponible : Dan FENG &amp; Vignoble décor</p>\n</div>', 'coffret-tire-bouchon-vin', '', '', '', 'Coffret Tire-Bouchon - Vin', '', ''),
(142, 1, 1, '<p>Ce coffret Wine Elegance se compose de :</p>\n<div class="rte">\n<p>- 1 tire bouchon en inox</p>\n<p>- 1 bouchon en inox</p>\n<p>- 1 étui en bois</p>\n</div>', '<div class="rte">\n<p>Ce coffret Wine Elegance se compose de :</p>\n<p>- 1 tire bouchon en inox</p>\n<p>- 1 bouchon en inox</p>\n<p>- 1 étui en bois</p>\n</div>', 'coffret-bois-tire-bouchon-avec-un-bouchon-en-inox', '', '', '', 'Coffret Bois Tire bouchon avec un bouchon en Inox', '', ''),
(143, 1, 1, '<p>Le Set Sommelier avec bouchon est composé de :</p>\n<div class="rte">\n<ul><li>1 bouchon silicone</li>\n<li>1 tire bouchon sommelier silicone anti-dérapant de 16 cm</li>\n</ul></div>', '<div class="rte">\n<p>Le Set Sommelier avec bouchon est composé de :</p>\n<ul><li>1 bouchon silicone</li>\n<li>1 tire bouchon sommelier silicone anti-dérapant de 16 cm</li>\n</ul></div>', 'set-tire-bouchon-sommelier-avec-bouchon', '', '', '', 'Set tire bouchon sommelier avec bouchon', '', ''),
(144, 1, 1, '<p>Un magnifique set de 4 accessoires à vin :</p>\n<div class="rte">\n<ul><li>1 x Tire-bouchon</li>\n<li>1 x Coupe capsule</li>\n<li>1 x Décapsuleur</li>\n<li>1 x Bouchon</li>\n</ul><ul><li>Matière : Zinc</li>\n</ul></div>', '<div class="rte">\n<p>Un magnifique set de 4 accessoires à vin :</p>\n<ul><li>1 x Tire-bouchon</li>\n<li>1 x Coupe capsule</li>\n<li>1 x Décapsuleur</li>\n<li>1 x Bouchon</li>\n</ul><ul><li>Matière : Zinc</li>\n</ul></div>', 'set-de-4-tire-bouchon-coupe-capsule-decapsuleur-bouchon', '', '', '', 'Set de 4', '', ''),
(145, 1, 1, '<p>Vintury vous propose actuellement un super set de 3 accessoires :</p>\n<div class="rte">\n<ul><li>1 x Tire-bouchon</li>\n<li>1 x Casse-noix</li>\n<li>1 x Décapsuleur</li>\n<li>Matière : Zinc</li>\n</ul></div>', '<div class="rte">\n<p>Vintury vous propose actuellement un super set de 3 accessoires :</p>\n<ul><li>1 x Tire-bouchon</li>\n<li>1 x Casse-noix</li>\n<li>1 x Décapsuleur</li>\n<li>Matière : Zinc</li>\n</ul></div>', 'set-de-3-tire-bouchon-casse-noix-decapsuleur', '', '', '', 'Set de 3 - Tire-bouchon / Casse-noix / Décapsuleur', '', '');
